import React, {Component} from 'react';

class Cart extends Component {
    state = {
        cart: [],
        isLoaded: false
    }
    //only want to fetch order when it's loaded
    componentDidMount() {
        axios.get('/cart.json')
            .then(res => {
                const fetchedOrders = [];
                for (let key in res.data) {
                    fetchedOrders.push({
                        ...res.data[key],
                        id: key
                    });
                }
                this.setState({isLoaded: true, cart: fetchedOrders});

            })
            .catch(err => {
                this.setState({isLoaded: true});
            })
    }
    render () {
        return (
            <div>

            </div>
        );
    }
}

export default Cart
