import React, {Component} from 'react';

import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import classes from './ItemCards.css';

class ItemCards extends Component {
    render() {
        let elements = []
        //id: pos + 1
        this.props.productsList.map(product => {
            elements.push(
                <div style={{
                    paddingTop: '30px',
                    paddingBottom: '30px'
                }}>
                    <Card 
                    style={{
                        width: '20rem',
                        paddingBottom: '30px' 
                        }}>
                    <Card.Img 
                        variant="top" 
                        src={product.image}
                        style={{
                            height: '300px',
                            paddingTop: '20px',
                            paddingLeft: '20px',
                            paddingRight: '20px'
                        }} 
                        />
                    <Card.Body style={{
                        paddingTop: '40px',
                        height: '150px',
                        }}>
                        <Card.Title>{product.title}</Card.Title>
                        ${product.price}
                    </Card.Body>
                    <div align="right"
                        style={{
                            paddingRight: '20px'
                    }}>
                    <Button variant="primary">Add to Cart</Button>
                    </div>
                    </Card>
                </div>
            )
        });

    return (
        <div className={classes.Cards}
            style={{
                paddingTop: '70px',
                paddingLeft: '100px'
            }}>
            <div class="row">
                {elements}
            </div>
        </div>
    )}
};

export default ItemCards;
