import React, {Component} from 'react';

import Axios from '../../axio-cart';
import CartItem from '../../components/Cart/CartItem/CartItem';

class Cart extends Component {
    state = {
        cart: [],
        isLoaded: false
    }

    componentDidMount() {
        Axios.get('/cart/5f63e617b29b17b8d1f854a7/find-cart-by-id')
            .then(res => res.data.products)
            .then (
                    (products) => {
                        this.setState({ cart: products, isLoaded: true })
                    })
    }

    render() {
        const cartPageDisplay = this.state.cart.map(cartItem => (<CartItem 
                                                                    key = {cartItem._id}
                                                                    title= {cartItem.title}
                                                                    image = {cartItem.image}
                                                                    price = {cartItem.price}/>))


        if (this.state.isLoaded === false) {
            cartPageDisplay = <Spinner />
        }
        return (
            <div>
                This is the cart
            </div>
        )
    }
}

export default Cart
