import axios from '../axio-cart';
import _ from 'lodash';

export const ADD_TO_CART = 'ADD_TO_CART';
export const REMOVE_FROM_CART = 'REMOVE_FROM_CART';
// export const GET_CART = 'GET_CART';
export const LOADING = 'LOADING';
export const GET_PRODUCT = 'GET_PRODUCT';
export const GET_PRODUCT_FAIL = 'GET_PRODUCT_FAIL';

export const getProduct = () => async (dispatch) => {
    dispatch({type: 'LOADING'});
    try {
        const res = await axios.get('/product/find-all-product')

        dispatch({type: 'GET_PRODUCT', payload: res})
    } catch(error){
        dispatch({type: 'GET_PRODUCT_FAIL', payload: error})
    }

}

export const addToCart = (id, productList) => async (dispatch) => {
    const cart = await axios.get('/cart/5f63e617b29b17b8d1f854a7/find-cart-by-id');
    const updatedCart = []
    const organizedCart = []
    const countArray = []
    const item = {}
    const totalPrice = 0
    console.log(cart)

    if (cart.data === []) {
        updatedCart = await axios.post('/cart/create-cart', {productID: id, userID: '5f63e617b29b17b8d1f854a7'})
    } else {
        updatedCart = await axios.put('/cart/add-to-cart', {productID: id, userID: '5f63e617b29b17b8d1f854a7'})
    }

    countArray = _.map(_.countBy(updatedCart), (val, key) => ({productID: key, count: val }));
    console.log(countArray)

    for (let i = 0; i < countArray.length; i++) {
        item = productList.find(product => product.productID === countArray[i].id)
        totalPrice += item.price * countArray[i].count
        item.count = countArray[i].count
        organizedCart.push(item);
    }


        dispatch({type: 'ADD_TO_CART', payload: [organizedCart, totalPrice]})
}

export const removeFromCart = (product) => async (dispatch) => {
    await axios.put('/cart/remove-from-cart', {productID: product._id, userID: '5f63e617b29b17b8d1f854a7'});
    const res = {}

    const updatedCart = await axios.get('/cart/5f63e617b29b17b8d1f854a7/group-product');
    res.cart = updatedCart;
    res.productPrice = product.price;

    dispatch({type: 'REMOVE_FROM_CART', payload: res})

}

