import axios from '../axio-cart';

export const ADD_TO_CART = 'ADD_TO_CART';
export const REMOVE_FROM_CART = 'REMOVE_FROM_CART';
export const PRODUCT = () => (dispatch) => {
    dispatch({type: PRODUCT});
    axios.get('/product/find-all-product')
    .then(res => {
        dispatch({type: ALL_PRODUCT, payload: res})
    })
    .catch((error)=> {
        dispatch({type: ALL_PRODUCT_FAIL, payload: error});
    })
}
