import React, {Component} from 'react';

import Toolbar from '../../components/Toolbar/Toolbar';
import Aux from '../../hoc/Aux/Aux';


class MainPage extends Component {
    state = {
        products: [],
        isLoaded: false
    }

    componentDidMount() {
        fetch('https://fakestoreapi.com/products')
            .then(res => res.json())
            .then(
                (json) => {
                    this.setState({products: json});
                }
            )

    }

    render() {
        let elements = []
        //id: pos + 1
        this.state.products.map(product => {
            elements.push(
                <div style={{
                    paddingTop: '30px',
                    paddingBottom: '30px',
                    paddingLeft: '30px',
                    paddingRight: '30px'
                }}>
                    <Card 
                    style={{
                        width: '20rem',
                        paddingBottom: '30px' 
                        }}>
                    <Card.Img 
                        variant="top" 
                        src={product.image}
                        style={{
                            height: '300px',
                            paddingTop: '20px',
                            paddingLeft: '20px',
                            paddingRight: '20px'
                        }} 
                        />
                    <Card.Body style={{
                        paddingTop: '40px',
                        height: '190px',
                        }}>
                        <Card.Title>{product.title}</Card.Title>
                        ${product.price}
                    </Card.Body>
                    <div align="right"
                        style={{
                            paddingRight: '20px'
                    }}>
                    <Button variant="primary">Add to Cart</Button>
                    </div>
                    </Card>
                </div>
            )
        });
        return (
            <Aux>
                <Toolbar />
                <div className={classes.Cards}
                        style={{
                            paddingTop: '30px',
                            paddingBottom: '30px',
                            paddingLeft: '150px'
                        }}>
                <div class="row">
                    {elements}
                </div>
                </div>
            </Aux>    
        )
    }
}

export default MainPage
