import React, {Component} from 'react';

import Toolbar from '../../components/Toolbar/Toolbar';
import ItemCards from '../../components/ItemCards/ItemCards.js';
import Aux from '../../hoc/Aux/Aux';


class MainPage extends Component {
    state = {
        products: [],
        itemsInCart: [],
        totalPrice: 0,
        isLoaded: false
    }

    componentDidMount() {
        fetch('https://fakestoreapi.com/products')
            .then(res => res.json())
            .then(
                (json) => {
                    this.setState({products: json});
                }
            )

    }

    addToCartHandler = (id) => {
        //returns product object with specified id
         const product = this.state.products.find(product => product.id == id)
         product.push(itemsInCart)
         const productPrice = product.price
         const currPrice = this.state.totalPrice
         const newPrice = currPrice + productPrice
         this.setState( {totalPrice: newPrice})

    }

    render() {
        return (
            <Aux>
                <Toolbar />
                <ItemCards productsList={this.state.products} addToCart={this.addToCartHandler}/>
            </Aux>    
        )
    }
}

export default MainPage
