import React, { Component } from 'react';

import classes from './Cart.module.css';
import Button from '../../components/UI/Button/Button';

class Cart extends Component {
    render () {
        let qtyCounter = []
        let elements = []

        if (this.props.itemsInCart.length == 0) {
            elements = "Cart is Empty"
        }

        this.props.itemsInCart.forEach(item => {
            let resObj = qtyCounter.find(resObj => resObj.id === item.id);
            resObj ? resObj.count++ : qtyCounter.push({'id': item.id, 'count': 1});
        }, () => console.log(qtyCounter))

        this.props.itemsInCart.map(product => {
            let qtyProduct = qtyCounter.map(item => item.id === product.id)
            elements.push(
            <div>
                <div className={classes.CartItem}>
                <span>
                    <img className={classes.ImageStyle}
                        src={product.image}
                        alt={'Image cannot be found'}/>
                    <span className={classes.TitleStyle}><strong>{product.title}</strong></span>
                    <span>Qty: {}</span>
                    <div className={classes.PriceStyle}>${(product.price).toFixed(2)}</div>
                </span>
            </div>
        </div>
            )
        })
        return (
            <div>
                {elements}
                <div>
                    <Button btnType= "Danger" clicked={this.props.exitCartView}>Cancel</Button>
                </div>
            </div>
        );
    }
}

export default Cart;
