import React, {Component} from 'react';

import Button from '../UI/Button/Button';
import classes from './CartSummary.module.css';
import CartSummaryCard from './CartSummaryCard/CartSummaryCard';
class CartSummary extends Component {

    render() {
    let elements = []

    if (this.props.cart.length === 0) {
        elements = "Cart is Empty"
    }

    this.props.cart.map(product => {
        elements.push(
            <CartSummaryCard product={product}/>
    )})                                     
    let subtotalField = <div style={{

                            }}>
                            Subtotal: ${this.props.price.toFixed(2)}
                        </div>

    let checkoutButton = <Button btnType="Success" clicked={this.props.checkoutContinued}>Checkout</Button>

    if (this.props.cart.length === 0) {
        subtotalField = null
    }

    return (
        <div className={classes.Cart}>
            {elements}
            {subtotalField}
            <div>
                {checkoutButton}
            </div>
        </div>
    )


    } 
}

export default CartSummary
