import React, {Component} from 'react';

import Axios from '../../axio-cart';
import CartItem from '../../components/Cart/CartItem/CartItem';

class Cart extends Component {
    state = {
        cart: [],
        isLoaded: false
    }

    componentDidMount() {
        Axios.get('/cart/5f63e617b29b17b8d1f854a7/find-cart-by-id')
            .then(res => res.data.products)
            .then (
                    (products) => {
                        this.setState({ cart: products, isLoaded: true })
                    })
    }

    render() {
        const cartPageDisplay = "Cart is Empty"

        if (this.state.cart.length() !== 0) {
            cartPageDisplay = this.state.cart.map(cartItem => (<CartItem 
                                                                    key = {cartItem._id}
                                                                    cartItem = {cartItem}/>))
        }


        if (this.state.isLoaded === false) {
            cartPageDisplay = <Spinner />
        }
        return (
            <div>
                {cartPageDisplay}
            </div>
        )
    }
}

export default Cart
