import axios from '../axio-cart';

export const ADD_TO_CART = 'ADD_TO_CART';
export const REMOVE_FROM_CART = 'REMOVE_FROM_CART';
export const GET_CART = 'GET_CART';
export const LOADING = 'LOADING';
export const GET_PRODUCT = 'GET_PRODUCT';
export const GET_PRODUCT_FAIL = 'GET_PRODUCT_FAIL';

export const getProduct = () => async (dispatch) => {
    dispatch({type: 'LOADING'});
    try {
        const res = await axios.get('/product/find-all-product')

        dispatch({type: 'GET_PRODUCT', payload: res})
    } catch(error){
        dispatch({type: 'GET_PRODUCT_FAIL', payload: error})
    }

}

export const addToCart = async (product) => {
    const cart = await axios.get('/cart/5f63e617b29b17b8d1f854a7/find-cart-by-id');
    console.log(product)
    const res = {}

    if (cart.data === []) {
        await axios.post('/cart/create-cart', {productID: product._id, userID: '5f63e617b29b17b8d1f854a7'})
    } else {
        await axios.put('/cart/add-to-cart', {productID: product._id, userID: '5f63e617b29b17b8d1f854a7'})
    }
}

export const removeFromCart = async (product) => {
    await axios.put('/cart/remove-from-cart', {productID: product._id, userID: '5f63e617b29b17b8d1f854a7'});

}

export const getCart = () => async(dispatch) => {
        const res = await axios.get('/5f63e617b29b17b8d1f854a7/group-product');
        console.log(res)

        if (res) {
            dispatch({type: 'GET_CART', payload: res})
        }

}
//import get product, map dispatch to the prop
