import React, {Component} from 'react';
import {store} from 'react-notifications-component';
import ReactNotification from 'react-notifications-component'
import 'react-notifications-component/dist/theme.css'

import 'animate.css/animate.compat.css'
import ItemCards from '../../components/ItemCards/ItemCards.js';
import Aux from '../../hoc/Aux/Aux';
import Spinner from '../../components/UI/Spinner/Spinner';
import Modal from '../../components/UI/Modal/Modal';
import MoreInfo from '../../components/MoreInfo/MoreInfo';
import Toolbar from '../../components/Toolbar/Toolbar';
import Cart from '../../components/Cart/Cart';


class MainPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            products: [],
            itemsInCart: [],
            totalPrice: 0,
            viewingMoreInfo: false,
            currentViewingProductId: 0,
            viewingCart: false,
            isLoaded: false
        }
        this.updateCart = this.updateCart.bind(this);
        this.createNotification = this.createNotification.bind(this);
    }

    componentDidMount() {
        fetch('https://fakestoreapi.com/products')
            .then(res => res.json())
            .then(
                (json) => {
                    this.setState({products: json, isLoaded: true});
                }
            )

    }

    addToCartHandler = (id, type) => {
        //returns product object with specified id
        this.updateCart(id);
        this.createNotification(type);

    }

    moreInfoClickHandler = (id) => {
        this.setState({viewingMoreInfo: true, currentViewingProductId: id});
    }

    moreInfoCancelHandler = () => {
        this.setState({viewingMoreInfo: false})
    }

    viewingCartHandler = () => {
        this.setState({viewingCart: true});
    }

    cancelViewingCartHander = () => {
        this.setState({viewingCart: false})
    }

    updateCart = (id) => {
        //check if it's already in cart; if pass this check, it wasn't in items array already
        if (this.state.itemsInCart.map(item => item.id === id)) {
            product.qty += 1
        } else {
            const product = this.state.products.find(product => product.id === id)
            product.qty = 1
            this.setState({ itemsInCart: this.state.itemsInCart.concat([product]) })

        }
        const productPrice = product.price
        const currPrice = this.state.totalPrice
        const newPrice = currPrice + productPrice

        this.setState( {totalPrice: newPrice} )
    }

    createNotification = () => {
        store.addNotification({
            title: 'Added to Cart',
            message: 'Item Successfully Added to Cart',
            type: 'success',                         // 'default', 'success', 'info', 'warning'
            container: 'top-right',                // where to position the notifications
            animationIn: ["animated", "fadeIn"],     // animate.css classes that's applied
            animationOut: ["animated", "fadeOut"],   // animate.css classes that's applied
            dismiss: {
              duration: 5000
            }
          })
    }


    render() {
        let mainPageDisplay =  <ItemCards 
                                productsList={this.state.products} 
                                moreInfo={this.moreInfoClickHandler}
                                />

        if (this.state.isLoaded === false) {
            mainPageDisplay = <Spinner />
        }

        let moreInfoSummary = null;

        if (this.state.currentViewingProductId !== 0) {
            moreInfoSummary = (
            <MoreInfo
                addToCart={this.addToCartHandler}
                moreInfoCancel={this.moreInfoCancelHandler}
                product ={this.state.products.find(product => product.id === this.state.currentViewingProductId)}/>
            )
        }

        if (this.state.viewingCart) {
            mainPageDisplay = <Cart 
                                    itemsInCart={this.state.itemsInCart}
                                    exitCartView={this.cancelViewingCartHander}
                                    />
        }

        return (
            <Aux>
                <Toolbar viewingCart={this.viewingCartHandler}/>
                <ReactNotification />
                <Modal show={this.state.viewingMoreInfo} modalClosed={this.moreInfoCancelHandler }>
                    {moreInfoSummary}
                </Modal>
                {mainPageDisplay}
            </Aux>    
        )
    }
}

export default MainPage
