import React, { Component } from 'react';

import classes from './Cart.module.css';
import Button from '../../components/UI/Button/Button';

class Cart extends Component {
    render () {
        let elements = []

        if (this.props.itemsInCart.length == 0) {
            elements = "Cart is Empty"
        }

        this.props.itemsInCart.map(product => {
            elements.push(
            <div>
                <div className={classes.CartItem}>
                <span>
                    <img className={classes.ImageStyle}
                        src={product.image}/>
                    <span>{product.title}</span>
                    <span>{product.price}</span>
                </span>
                <div>
                    <Button btnType= "Danger" clicked={this.props.exitCartView}>Cancel</Button>
                </div>
            </div>
        </div>
            )
        })
        return (
            <div>
                {elements}
            </div>
        );
    }
}

export default Cart;
