import React, {Component} from 'react';

import Button from '../UI/Button/Button';
import classes from './CartSummary.module.css';
import Quantity from '../Quantity/Quantity';

class CartSummary extends Component {

    render() {
    let elements = []

    if (this.props.cart.length === 0) {
        elements = "Cart is Empty"
    }

    this.props.cart.map(product => {
        if (product.count > 0) {
            elements.push(
                    <div className={classes.CartItem}>
                    <span>
                        <img className={classes.CartImage}
                            src={product.image}
                            alt={'Image cannot be found'}/>
                        <span style={{
                            position: 'relative',
                            bottom: '40px',
                            paddingLeft: '15px'
                        }}><strong>{product.title}</strong></span>
                        <span><Quantity min={0} max={99} product={product} decrement={this.props.decrement} increment={this.props.increment}/></span>
                        <div style={{
                             position: 'relative',
                             bottom: '80px',
                             paddingLeft: '135px'
                        }}>${(product.price).toFixed(2)}</div>

                        </span>
                        </div>
            )
        } 
    })          
    let subtotalField = <div>
                            Subtotal: ${(this.props.price).toFixed(2)}
                        </div>

    let checkoutButton = <Button btnType="Success" clicked={this.props.checkoutContinued}>Checkout</Button>

    if (this.props.cart.length === 0) {
        subtotalField = null
        checkoutButton = null
    }

    return (
        <div className={classes.Cart}>
            <div className={classes.CartInnerDiv}>
                {elements}
                <div className={classes.Subtotal}>
                    {subtotalField} 
                </div>
                <div className={classes.Checkout}>
                    {checkoutButton}
                </div>
            </div>
        </div>
    )


    } 
}

export default CartSummary
