import React, {Component} from 'react';

import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import classes from './ItemCards.css';
import Row from 'react-bootstrap/Row';

class ItemCards extends Component {
    render() {
        let elements = []
        //id: pos + 1
        this.props.productsList.map(product => {
            elements.push(
                <div style={{
                    paddingTop: '30px',
                    paddingBottom: '30px',
                    paddingLeft: '30px',
                    paddingRight: '30px'
                }}>
                    <Card 
                    style={{
                        width: '20rem',
                        paddingBottom: '30px' 
                        }}>
                    <a href="itemInformation">
                    <Card.Img 
                        variant="top" 
                        src={product.image}
                        style={{
                            height: '300px',
                            paddingTop: '20px',
                            paddingLeft: '20px',
                            paddingRight: '20px'
                        }} 
                        />
                    <Card.Body style={{
                        paddingTop: '40px',
                        height: '190px',

                        }}>
                        <Card.Title>{product.title}</Card.Title>
                        ${product.price}
                    </Card.Body>
                    </a>
                    <div align="right"
                        style={{
                            paddingRight: '20px'
                    }}>
                    <Button variant="primary" onClick={() => this.props.addToCart(product.id)}>Add to Cart</Button>
                    </div>
                    </Card>
                </div>
            )
        });

    return (
        <div className={classes.Cards}
            style={{
                paddingTop: '30px',
                paddingBottom: '30px',
                paddingLeft: '150px'
            }}>
            <Row>
                {elements}
            </Row>
        </div>
    )}
};

export default ItemCards;
