import React, {Component} from 'react';

import Axios from '../../axio-cart';
import Spinner from '../../components/UI/Spinner/Spinner';

class Cart extends Component {
    state = {
        cart: [],
        totalPrice: 0,
        isLoaded: false
    }

    componentDidMount() {
        Axios.get('/cart/5f63e617b29b17b8d1f854a7/find-cart-by-id')
            .then(res => res.data)
            .then (
                    (data) => {
                        this.setState({ cart: data[0], totalPrice: data[1], isLoaded: true })
                    })
    }

    render() {
        let elements = []

        if (this.state.cart.length == 0) {
            elements = "Cart is Empty"
        }

        this.state.cart.map(product => {
            elements.push(
            <div>
                <div style={{
                    width: '1200px',
                    height: '150px',
                    border: '1px solid #ccc',
                    boxShadow: '0 2px 3px #ccc',
                    padding: '10px',
                    margin: '10px auto',
                    boxSizing: 'border-box'
                }}>
                <span>
                    <img style={{
                        width: '10%',
                        height: '90%'
                        }}
                        src={product.image}
                        alt={'Image cannot be found'}/>
                    <span style={{
                        position: 'relative',
                        bottom: '40px',
                        paddingLeft: '15px'
                    }}><strong>{product.title}</strong></span>
                    <span>Qty: {product.count}</span>
                    <div style={{
                         position: 'relative',
                         bottom: '80px',
                         paddingLeft: '135px'
                    }}>${(product.price).toFixed(2)}</div>
                </span>
            </div>
        </div>  
        )})                                     

        if (this.state.isLoaded === false) {
            elements = <Spinner />
        }
        return (
            <div>
                {elements}
                <div>

                </div>
            </div>
        )
    }
}

export default Cart
